## Create ManagerModule.kt

File: app/src/main/java/com/cactuvi/app/di/ManagerModule.kt

```kotlin
package com.cactuvi.app.di

import android.content.Context
import com.cactuvi.app.data.db.AppDatabase
import com.cactuvi.app.data.sync.ReactiveUpdateManager
import com.cactuvi.app.utils.CredentialsManager
import com.cactuvi.app.utils.PreferencesManager
import com.cactuvi.app.utils.SourceManager
import com.cactuvi.app.utils.SyncPreferencesManager
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.android.qualifiers.ApplicationContext
import dagger.hilt.components.SingletonComponent
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object ManagerModule {

    @Provides
    @Singleton
    fun provideAppDatabase(
        @ApplicationContext context: Context
    ): AppDatabase {
        return AppDatabase.getInstance(context)
    }

    @Provides
    @Singleton
    fun providePreferencesManager(
        @ApplicationContext context: Context
    ): PreferencesManager {
        return PreferencesManager.getInstance(context)
    }

    @Provides
    @Singleton
    fun provideCredentialsManager(
        @ApplicationContext context: Context
    ): CredentialsManager {
        return CredentialsManager.getInstance(context)
    }

    @Provides
    @Singleton
    fun provideSourceManager(
        @ApplicationContext context: Context
    ): SourceManager {
        return SourceManager.getInstance(context)
    }

    @Provides
    @Singleton
    fun provideSyncPreferencesManager(
        @ApplicationContext context: Context
    ): SyncPreferencesManager {
        return SyncPreferencesManager.getInstance(context)
    }

    @Provides
    @Singleton
    fun provideReactiveUpdateManager(): ReactiveUpdateManager {
        return ReactiveUpdateManager.getInstance()
    }
}
```

